mapper.select:

select * 
  from tbl_attach 
 where master_id = #{masterId}
   and master_name = #{masterName}

mapper.delete:

delete from tbl_attach 
 where uuid=#{uuid}
   and master_id = #{masterId}
   and master_name = #{masterName}


delete from tbl_attach 
 where master_id = #{masterId}
   and master_name = #{masterName}
